import React, {Component} from 'react';

class CreatePost extends Component {

    render () {
        return (
            <form className="add-post">
                <input style={{ font: '19px'}} 
                type="text"
                placeholder="Title" name="postTitle">
                <textarea 
                   type="text"
                   name="postBody"
                   rows="3"
                   cols="40"
                   required
                   placeholder="New Blog Post"/>
                <input type="submit" 
                className="btn"
                style={{fontSize: '19px'}} /> 
            </input>
            </form >
        )
    }
}
export default CreatePost;
