import React, {Component} from 'react';
import { Auth } from 'aws-amplify';

class EditPost extends Component {

state = {
    show: false,
    id: "",
    postOwnerId: "",
    postTitle:"",
    postBody: "",
    postOwnerUsername: "",
    postData: {
        postTitle: this.props.postTitle,
        postBody: this.props.postBody
    }
}

handleModal = () =>{
    this.setState({show:!this.state.show})
    document.body.scrollTop = 0
    document.documentElement.scrollTop = 0
}

handleTitle=()=>{
    this.setState({
        postData: {...this.state.postData, postTitle:event.target.value}
    })
}

handleBody = () =>{
    this.setState({
        postData: {...this.state.postData, postBody:event.target.value}
    })

}

componentWillMount = async () =>{
    await Auth.currentUserInfo()
    .then(
        user => {
            this.setState({
                postOwnerId: user.attributes.sub,
                postOwnerUsername: user.username
            })
        }
    )
}

    render () {
        return (
            <>

            {
                this.state.show && (
                    <div className="modal">
                        <button className="close" onClick={this.handleModal}>
                            X
                        </button>
                        <form className="add-post" onSubmit={(event) => this.handleUpdatePost(event)}>
                            <input style={{fontSize: "19px"}}
                                   type="text" 
                                   placeholder="Title"
                                   name="postTitle"
                                   value={this.state.postData.postTitle}
                                   onChange={this.handleTitle}/>

                            <input 
                              style={{height:"150px", fontSize:"19px"}} 
                              type = "text"
                              name="postBody"
                              value={this.state.postData.postBody}
                              onChange={this.hadleBody} 
                              />

                            <button>Update Post</button>
                        </form>

                    </div>
                )
            }


            <button onClick={this.handleModal}>

                Edit
            </button>
            </>
        )
    }
}
export default EditPost;
