import React, { Component } from 'react'
import { listPosts } from '../graphql/queries';
import { API, graphqlOperation} from 'aws-amplify'
import DeletePost from './DeletePost';
import EditPost from './EditPost';
import { onCreatePost, onDeletePost } from '../graphql/subscriptions';
class DisplayPosts extends Component {

    state = {
        posts: []
    }

    componentDidMount = async () => {
        this.getPosts();

        this.createPostListener = API.graphql(graphqlOperation(onCreatePost))
        .subscribe({
            next: postData => {
                const newPost = postData.value.data.onCreatePost
                const prevPosts = this.state.posts.filter( post => post.id !== newPost.id)
                const updatedPosts = [newPost, ...prevPosts]
                this.setState({posts: updatedPosts})
            }
        })

        this.deletePostListner = API.graphql(graphqlOperation(onDeletePost))
        .subscribe({
            next: postData =>{
                const deletedPost = postData.value.data.onDeletePost
                const updatedPosts = this.state.posts.filter(post => post.id !== deletedPost.id)
            }
        })
    }

    componentWillUnmount(){
        this.createPostListener.unsubscribe()
    }

    getPosts = async () => {
        const result = await API.graphql(graphqlOperation(listPosts));
        //console.log("All Posts: ", JSON.stringify(result.data.listPosts.items));
        //console.log("All Posts: ", result.data.listPosts.items);
        this.setState({posts: result.data.listPosts.items})
    }

    render() { 
        const {posts} = this.state;
        return posts.map((post) => {
            return(
                <div className="posts" style={rowStyle} key = {post.id}>
                    <h1>{post.postTitle}</h1>
                    <span style={{ fontStyle: "italic", color:"#0ca5e297" }}>
                        {"Postado Por: "} {post.postOwnerUsername}
                        {" on "}
                        <time style={{ fontStyle: "italic"}}>
                            {" "}
                            { new Date(post.createdAt).toDateString()}
                        </time>
                    </span>
                    <p>
                        {post.postBody}

                    </p>
                    <br />
                    <span>
                        <DeletePost data= {post}/>
                        <EditPost />
                    </span>
                </div>
            )
        })
    }
}

const rowStyle = {

    background: '#f4f4f4',
    padding: '10px',
    border: '1px #ccc dotted',
    margin: '14px'

}

export default DisplayPosts;
